mapper.select:
SELECT 
FROM t_blog_description blog_description_
WHERE blog_description_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_blog_description
article_id = #{articleId,jdbcType=BIGINT}, title = #{title,jdbcType=VARCHAR}, description = #{description,jdbcType=VARCHAR}, word = #{word,jdbcType=INTEGER}, keyword = #{keyword,jdbcType=VARCHAR}, prefer = #{prefer,jdbcType=INTEGER}, click_count = #{clickCount,jdbcType=INTEGER}, author_id = #{authorId,jdbcType=BIGINT}, status = #{status,jdbcType=INTEGER}, update_date = #{updateDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
