mapper.select:
SELECT

FROM t_mall_area mall_area_
WHERE mall_area_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_mall_area
area_code = #{areaCode,jdbcType=BIGINT}, short_code = #{shortCode,jdbcType=VARCHAR}, area_name = #{areaName,jdbcType=VARCHAR}, tag = #{tag,jdbcType=VARCHAR}, note = #{note,jdbcType=VARCHAR},
WHERE id = #{id,jdbcType=BIGINT}
