mapper.select:
		SELECT 
        FROM t_mall_question mall_question_
        WHERE mall_question_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_mall_question
question = #{question,jdbcType=VARCHAR}, answer = #{answer,jdbcType=VARCHAR}, reason = #{reason,jdbcType=VARCHAR}, note = #{note,jdbcType=VARCHAR}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = #{updateDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
