mapper.select:
SELECT

FROM sys_menu m_
WHERE m_.id = #{id,jdbcType=BIGINT}

SELECT

FROM sys_menu m_
WHERE m_.system_id = #{systemId}
AND m_.state !=0
AND m_.level=#{level}
ORDER BY m_.sort ASC

SELECT

FROM sys_menu m_
WHERE m_.parent_id = #{parentId}
and m_.system_id = #{systemId}
ORDER BY m_.sort ASC

mapper.update:
UPDATE sys_menu
code = #{code,jdbcType=VARCHAR}, `name` = #{name,jdbcType=VARCHAR}, name_english = #{nameEnglish,jdbcType=VARCHAR}, parent_id = #{parentId,jdbcType=BIGINT}, sort = #{sort}, `level` = #{level}, `state` = #{state}, url = #{url,jdbcType=VARCHAR}, icon = #{icon,jdbcType=VARCHAR}, note = #{note,jdbcType=VARCHAR}, system_id = #{systemId,jdbcType=BIGINT}, create_date = #{createDate,jdbcType=TIMESTAMP}, update_date = #{updateDate,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
