mapper.delete:
DELETE FROM t_sys_file

    id = #{id}
    version = #{version}
    code = #{code}
    `name` = #{name}
    suffix = #{suffix}

mapper.select:
SELECT

FROM t_sys_file sys_file_
WHERE sys_file_.id = #{id,jdbcType=BIGINT}

SELECT

FROM t_sys_file sys_file_
WHERE
sys_file_.code = #{code,jdbcType=VARCHAR}
or sys_file_.id = #{code}
or sys_file_.name= #{code}

SELECT

FROM t_sys_file sys_file_
WHERE
uri = #{uri,jdbcType=VARCHAR}
code = #{code}

mapper.update:
UPDATE t_sys_file
`name` = #{name,jdbcType=VARCHAR}, suffix = #{suffix,jdbcType=VARCHAR}, version = #{version,jdbcType=INTEGER}, code = #{code,jdbcType=VARCHAR}, `key` = #{key,jdbcType=VARCHAR}, `length` = #{length,jdbcType=BIGINT}, length_unit = #{lengthUnit}, path = #{path}, location = #{location}, host = #{host}, uri = #{uri,jdbcType=VARCHAR}, thumbnail_uri = #{thumbnailUri}, note = #{note,jdbcType=VARCHAR}, check_type = #{checkType,jdbcType=VARCHAR}, `type` = #{type}, check_code = #{checkCode,jdbcType=VARCHAR}, state = #{state,jdbcType=INTEGER}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_time = #{updateTime,jdbcType=TIMESTAMP},
WHERE id = #{id,jdbcType=BIGINT}
