mapper.select:
		SELECT 
        FROM t_we_chat_access_token we_chat_access_token_
        WHERE we_chat_access_token_.id = #{id,jdbcType=BIGINT}

mapper.update:
UPDATE t_we_chat_access_token
access_token = #{weChatAccessToken,jdbcType=VARCHAR}, corp_id = #{corpId,jdbcType=VARCHAR}, corp_secret = #{corpSecret,jdbcType=VARCHAR}, expires_in = #{expiresIn,jdbcType=BIGINT}, remark = #{remark,jdbcType=VARCHAR}, response = #{response,jdbcType=VARCHAR}, state = #{state,jdbcType=INTEGER}, update_time = #{updateTime,jdbcType=TIMESTAMP}, version = #{version,jdbcType=INTEGER},
WHERE id = #{id,jdbcType=BIGINT}
